const messages = [
  "Learn React ‚öõÔ∏è",
  "Apply for jobs üíº",
  "Invest your new income ü§ë",
];
export default function App() {
  const step = 1;

  function previous(){
    alert("Previous")
  }
  function next(){
    alert("next")
  }
  return (
    <div className="steps">
      <div className="numbers">
        <div className={step >= 1 ? "active" : ""}>1</div>
        <div className={step >= 2 ? "active" : ""}>2</div>
        <div className={step >= 3 ? "active" : ""}>3</div>
      </div>
      <p className="message">Stop{step}:{messages[step -1]}</p>

      <div className="buttons">
        <button style={{backgroundColor: "#7950f2"}} onClick={previous}>Previous</button>
        <button style={{backgroundColor: "#7950f2"}} onClick={next}>Next</button>
      </div>
    </div>
  );
}
